class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int n = a.size(), m = q.size();
        int ans = 0;
        for(int j = 0; j < n; j++) {
            vector<vector<int> > dp(m, vector<int>(a[j]+1, -1));
            auto dfs = [&](auto& dfs, int i, int y) -> int {
                if(i == m) {
                    if(y == 0) return 0;
                    else return m + 1;
                }
                if(y == 0) return 0;
                if(dp[i][y] != -1) return dp[i][y];
                int res = dfs(dfs, i + 1, y) + 1;
                if(q[i][0] <= j and j <= q[i][1] and y >= q[i][2]) 
                    res = min(res, dfs(dfs, i + 1, y - q[i][2]) + 1);
                return dp[i][y] = res;
            };
            int t = dfs(dfs, 0, a[j]);
            if(t >= m + 1) return -1;
            ans = max(ans, t);
        }
        if(ans == m + 1) ans = -1;
        return ans;
    }
};

