class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        const int n = nums.size(), m = queries.size();
        vector<set<int>> arr(n, set<int>{0});
        vector<int> ret(n, INT_MAX);
        for (int i = 0; i < n; ++i) {
            if (nums[i] == 0) {
                ret[i] = 0;
            }
        }
        vector<int> v;
        for (int i = 0; i < m; ++i) {
            const int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            for (int j = l; j <= r; ++j) {
                if (ret[j] != INT_MAX) {
                    continue;
                }
                auto& s = arr[j];
                v.clear();
                for (auto t : s) {
                    if (t + val == nums[j]) {
                        ret[j] = i + 1;
                        break;
                    }
                    if (t + val > nums[j]) {
                        break;
                    }
                    v.push_back(t + val);
                }
                s.insert(v.begin(), v.end());
            }
        }
        int r = *max_element(ret.begin(), ret.end());
        return r == INT_MAX ? -1 : r;
    }
};
