class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<pair<int, int>>> cover(n); // 
        // 
        for (int j = 0; j < queries.size(); ++j) {
            auto& q = queries[j];
            int l = q[0], r = q[1], val = q[2];
            for (int i = l; i <= r; ++i) {
                cover[i].emplace_back(j, val);
            }
        }
        // 
        for (auto& v : cover) {
            sort(v.begin(), v.end());
        }
        int max_j = -1;
        for (int i = 0; i < n; ++i) {
            int target = nums[i];
            if (target == 0) {
                continue; // max_j
            }
            unordered_map<int, int> dp;
            dp[0] = -1; // 0
            for (auto& p : cover[i]) {
                int j = p.first;
                int val = p.second;
                unordered_map<int, int> new_dp(dp); // 
                // 
                for (auto& [s, current_max] : dp) {
                    int new_s = s + val;
                    if (new_s > target) continue;
                    int new_max = max(current_max, j); // 
                    // new_dp
                    if (new_dp.find(new_s) == new_dp.end()) {
                        new_dp[new_s] = new_max;
                    } else {
                        if (new_max < new_dp[new_s]) {
                            new_dp[new_s] = new_max;
                        }
                    }
                }
                dp.swap(new_dp);
            }
            // 
            if (dp.find(target) == dp.end()) {
                return -1;
            }
            max_j = max(max_j, dp[target]);
        }
        // 0
        if (max_j == -1) {
            for (int num : nums) {
                if (num != 0) return -1;
            }
            return 0;
        }
        return max_j + 1;

    }
};

