class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n=nums.length;
        int m=queries.length;
        int k=0;
        for(int i=0;i<n;i++){
            int tar=nums[i];
            if(tar==0)continue;
            List<Integer> subs=new ArrayList<>();
            int sum=0;
            boolean zero=false;
            for(int j=0;j<m;j++){
                if(queries[j][0]<=i&&queries[j][1]>=i&&queries[j][2]<=tar){
                    subs.add(queries[j][2]);
                    sum+=queries[j][2];
                    if(sum>=tar&&canSum(subs,tar)){
                        k=Math.max(k,j+1);
                        zero=true;
                        break;
                    }
                }
            }
            if(!zero)return -1;
        }
        return k;
    }
    private boolean canSum(List<Integer> subs,int target){
        boolean[] f=new boolean[target+1];
        f[0]=true;
        for(int num:subs){
            for(int i=target;i>=num;i--){
                f[i]|=f[i-num];
            }
        }
        return f[target];
    }
}

