                    continue;
                }

                if(nums[i]-queries[j][2]>=0 && m[nums[i]-queries[j][2]] != 0){
                    if(res < j+1){
                        res = j+1;
                    }
                    f = true;
                    break;
                }

                int[] m2 = new int[1001];
                m2[queries[j][2]]=1;
                for(int k =0;k<1001;k++){
                    if(m[k]!=0){
                        if(k+queries[j][2]<1001) {
                            m2[k + queries[j][2]] = 1;
                        }
                        m2[k] = 1;
                    }
                }
                m = m2;
            }
            if(!f){
                return -1;
            }
        }
        return res;
    }

