class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        int a = 0;
        for (int i = 0; i < n; i++) {
            int x = nums[i];
            if (x == 0) {
                continue ;
            }
            memset(dp, 0, sizeof(dp));
            dp[0] = 1;

            for (int k = 0; k < m; k++) {
                int l = queries[k][0], r = queries[k][1], y = queries[k][2];
                if (i < l || i > r) {
                    continue ;
                }
                for (int j = x; j - y >= 0; j--) {
                    if (dp[j - y]) {
                        dp[j] = 1;
                    }
                }
                if (dp[x] == 1) {
                    a = max(a, k + 1);
                    break ;
                }
            }
            if (dp[x] != 1) {
                return -1;
            }
        }
        return a;
    }
};

