class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int sum = 0;
        for(auto c:nums){
            if(c != 0){
                sum = 1;
                break;
            }
        }
        if(sum == 0) return 0;
        int res = 0;
        for(int i=0;i<nums.size();i++){
            int len = nums[i];
            vector<int> dp(len+1,0);
            int cnt = 0;
            for(auto &c:queries){
                cnt++;
                if(i>=c[0] && i<=c[1]){
                    for(int k=len;k>=0;k--){
                        if(k >= c[2])
                            dp[k] = max(dp[k], dp[k-c[2]] + c[2]);
                    }
                    if(dp[len] == len){
                        res = max(res,cnt);
                        break;
                    }
                }
            }
            if(dp[len] < len) return -1;
        }
        return res;
    }
};

