class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<int>> p(n, vector<int>());
        vector<vector<int>> dp(n, vector<int>());
        for (int i = 0; i < n; ++i) {
            int m = nums[i];
            dp[i].resize(m + 1, 0);
            dp[i][0] = 1;
        }
        for (auto& q : queries) {
            int l = q[0], r = q[1], v = q[2];
            for (int i = 0; i < n; ++i) {
                if(i>=l&&i<=r)
                    p[i].push_back(v);
                else
                    p[i].push_back(0);
            }
        }
        int ans = -1;
        for (int i = 0; i < n; ++i) {
            int m = nums[i];
            int cnt = 1;
            if(m==0) {
                ans = max(ans,0);
                continue;
            }
            for (auto& x : p[i]) {
                if(x==0||x>m) {
                    ++cnt;
                    continue;
                }
                for (int j = m; j >= x; --j) {
                    dp[i][j] = dp[i][j] | dp[i][j - x];
                }
                if(dp[i][m]==1){
                    ans = max(ans,cnt);
                    // cout<<ans<<endl;
                    break;
                }
                ++cnt;
            }
            if(!dp[i][m]) return -1;
        }
        return ans;
    }
};

