  int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
    int sum = accumulate(nums.begin(), nums.end(), 0);
    if (sum == 0) return 0;
    const int n = nums.size();
    const int q = queries.size();
    // printf("n=%d q=%dn", n, q);
    vector<int> weights(q);
    vector<int> dp(q * 10);
    auto CheckV = [&](int p, int V, int mid) -> bool {  // 
      weights.clear();
      // queries  mid 
      for (int i = 0; i <= mid; i++) {
        auto& v = queries[i];
        int l = v[0], r = v[1], val = v[2];
        if (p < l || p > r || val > V) continue;
        if (val == V) return true;  // 
        weights.push_back(val);
      }
      sort(weights.begin(), weights.end());
      //  01 V 
      dp.clear();
      dp.resize(V + 1, 0);
      for (auto v : weights) {
        for (int i = V; i >= v; i--) {
          dp[i] = max(dp[i], dp[i - v] + v);
        }
      }
      return dp[V] == V;
    };
    auto Check = [&](int mid) -> bool {
      for (int i = 0; i < n; i++) {
        int v = nums[i];
        if (v == 0) continue;
        if (CheckV(i, v, mid)) continue;
        return false;
      }
      return true;
    };

    int l = 0, r = q;
    while (l < r) {
      int mid = (l + r) / 2;
      int ret = Check(mid);
      // printf("l=%d r=%d mid=%d ret=%dn", l, r, mid, ret);
      if (ret) {
        r = mid;
      } else {
        l = mid + 1;
      }
    }
    if (l == q) return -1;
    return l + 1;
  }
};

