class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)
        d = [[0] * m for _ in range(n)]
        for i, (l, r, val) in enumerate(queries):
            for j in range(l, r + 1):
                d[j][i] = val
        ans = []
        for i, row in enumerate(d):
            f = [inf] * (nums[i] + 1)
            f[0] = 0
            for k, x in enumerate(row, 1):
                if x == 0: continue
                for j in range(nums[i], -1, -1):
                    if j + x > nums[i] or f[j] == inf: continue
                    f[j + x] = min(f[j + x], k)
            ans.append(f[-1])
        mx = max(ans) if ans else inf
        return mx if mx < inf else -1

