class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        hm = defaultdict(list)
        for qi,(l,r,v) in enumerate(queries):
            for i in range(l,r+1):
                hm[i].append((v,qi+1))
        res = 0
        for i,n in enumerate(nums):
            if n == 0:
                continue
            if i not in hm:
                return -1
            q = {0}
            for v,qi in hm[i]:
                q = set(x for y in q for x in [y, y+v] if x <= n)
                if n in q:
                    res = max(res, qi)
                    break
            if n not in q:
                return -1
        return res

