class Solution
{
public:
    int minZeroArray(vector<int> &nums, vector<vector<int>> &queries) {
        int n = nums.size();
        int m = queries.size();

        int cnt_zero = 0;

        // dp[k][i]  k  i 
        vector<unordered_set<int>> dp(n);

        //  dp[0][i] = nums[i]
        for (int i = 0; i < n; i++)
        {
            cnt_zero += nums[i] == 0;
            dp[i].insert(nums[i]);
        }

        if (cnt_zero == n) return 0;

        // 
        for (int k = 0; k < m; k++)
        {
            int l = queries[k][0];
            int r = queries[k][1];
            int val = queries[k][2];

            // 
            vector<unordered_set<int>> new_dp = dp;

            // 
            for (int i = 0; i < n; i++)
            {
                if (i >= l && i <= r)
                {
                    //  i  val 
                    unordered_set<int> new_values;
                    for (int x : dp[i])
                    {
                        if (x - val >= 0)
                        {
                            new_values.insert(x - val); //  val
                        }
                        new_values.insert(x); //  val
                    }
                    new_dp[i] = new_values;
                }
            }

            //  dp
            dp = new_dp;

            //  0
            bool isZero = true;
            for (int i = 0; i < n; i++)
            {
                if (dp[i].find(0) == dp[i].end())
                {
                    isZero = false;
                    break;
                }
            }
            if (isZero)
            {
                return k + 1; //  + 1
            }
        }
        return -1;
    }
};

