int dijstra(vector<vector<int>>& grid) {
    priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> q;
    int n = grid.size(), m = grid[0].size();
    vector<vector<int>> vis(n, vector<int>(m, INT_MAX));
    int dxy[4][2] = {{0, 1}, {0, -1}, {1, 0}, {-1, 0}};

    vis[0][0] = 0;
    q.push({0, 0});
    while (!q.empty()) {
        auto [v, idx] = q.top(); q.pop();
        int x = idx/m, y = idx % m;

        for (int i = 0; i < 4; i++) {
            int xx = x + dxy[i][0];
            int yy = y + dxy[i][1];

            if (xx < 0 || yy >= m || xx >= n || yy < 0) continue;
            int mx = max(vis[x][y], grid[xx][yy]) + 1 + (x + y) % 2;
            if (vis[xx][yy] > mx) {
                vis[xx][yy] = mx;
                q.push({vis[xx][yy], xx*m + yy});
            }
        }
    }

    return vis[n - 1][m - 1];
}

vector<vector<int>> floyd(int n, vector<vector<int>>& edges) {
    vector<vector<int>> g(n, vector<int>(n, INT_MAX/2));
    vector<vector<int>> dis(n + 1, vector<int>(n + 1));

    for (auto &e : edges) {
        g[e[0]][e[1]] = g[e[1]][e[0]] = e[2];
    }

    dis = g;
    for (int k = 0; k < n; k++) {
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                dis[i][j] = fmin(dis[i][j], dis[i][k] + dis[k][j]);
            }
        }
    }

    return dis;
}

