static vector<int> getNext(const string& str) {
    int n = (int)str.size();
    vector<int> next(n);
    int j = 0;
    for (int i = 1; i < n; i++) {
        while (j > 0 && str[i] != str[j]) {
            j = next[j - 1];
        }
        if (str[i] == str[j]) {
            j++;
            next[i] = j;
        }
    }
    return next;
}

static vector<int> kmp(const string& a, const string& b) {
    if (a.size() < b.size()) {
        return {};
    }
    int n = (int)a.size();
    int m = (int)b.size();
    vector<int> res;
    vector<int> next = getNext(b);
    for (int i = 0, j = 0; i < n; i++) {
        while (j - 1 >= 0 && a[i] != b[j]) {
            j = next[j - 1];
        }
        if (a[i] == b[j]) {
            j++;
        }
        if (j == m) {
            res.push_back(i - j + 1);
            j = next[j - 1]; 
        }
    }
    return res;
}


