struct Trie {
	bool isEnd;
    Trie* children[26];

    Trie() {
        isEnd = false;
        memset(children, 0, sizeof(children));
    }

    void TrieInsert(const string &word) {
        Trie* node = this;
        for (char c : word) {
            if (node->children[c - 'a'] == NULL) {
                node->children[c - 'a'] = new Trie();
            }
            node = node->children[c - 'a'];
        }

        node->isEnd = true;
        return;
    }

    bool TrieSearch(const string &word) {
        Trie* node = this;
        for (char c : word) {
            if (node->children[c - 'a'] == NULL)
                return false;
            node = node->children[c - 'a'];
        }

        return node->isEnd;
    }
};


class FenwickTree {
    vector<int> tree;
public :
    FenwickTree(int n) : tree(n + 1) {}

    void update(int i, int val) {
        for (; i < tree.size(); i += i & -i) {
            tree[i] += val;
        }
        return ;
    } 

    int query(int i) {
        int sum = 0;
        for (; i > 0; i -= i & -i) {
            sum += tree[i];
        }
        return sum;
    }
};

