class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int ans = 1;
        vector<map<int, int>> g(n);

        for (int i = 0; i < n; i++) {
            g[i][nums[i]]++;
        }

        bool ff = true;
        for (int i = 0; i < n; i++) {
            if (g[i].find(0) == g[i].end()) {
                ff = false;
                break;
            }
        }

        if (ff == true) return 0;

        for (auto &q : queries) {
            int l = q[0], r = q[1], val = q[2];
            for (int i = l; i <= r; i++) {
                for (auto &[u, v] : g[i]) {
                    g[i][u - val]++;
                }
            }

            bool f = true;
            for (int i = 0; i < n; i++) {
                if (g[i].find(0) == g[i].end()) {
                    f = false;
                    break;
                }
            }

            if (f == true) return ans;
            ans++;
        }

        return -1;
    }
};

