class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), q = queries.size();
        if(accumulate(nums.begin(), nums.end(), 0) == 0) {
            return 0;
        }
        vector<vector<int>> dp(n, vector<int>(1010));
        for(int i = 0; i < n; i ++) dp[i][0] = 1;
        for(int i = 0; i < q; i ++) {
            int l = queries[i][0], r = queries[i][1], v = queries[i][2];
            for(int k = l; k <= r; k ++) {
                for(int j = nums[k]; j >= v; j --) {
                    dp[k][j] |= dp[k][j - v];
                }
            }
            int cnt = 0;
            for(int j = 0; j < n; j ++) {
                if(dp[j][nums[j]] == 1) cnt ++;
            }
            if(cnt == n) {
                return i + 1;
            }
        }
        return -1;
    }
};

