class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int ans = -1;
        for (int k = 0; k < nums.length; k++) {
            if (0 == nums[k]) {
                continue;
            }
            ArrayList<Integer> list = new ArrayList<>();
            for (int m = 0; m < queries.length; m++) {
                if (k <= queries[m][1] && queries[m][0] <= k) {
                    list.add(m);
                }
            }
            HashMap<Integer, Integer> hashMap = getIntegerHashMap(nums, queries, list, k);
            if (!hashMap.containsKey(nums[k])) {
                return -1;
            }
            ans = Math.max(hashMap.get(nums[k]), ans);
        }
        if (ans == -1) {
            long count = Arrays.stream(nums).filter(num -> num != 0).count();
            if (count > 0) {
                return -1;
            }
            return 0;
        }
        ans += 1;
        return ans;
    }

    public HashMap<Integer, Integer> getIntegerHashMap(int[] nums, int[][] queries,
                                                       ArrayList<Integer> list, int k) {
        HashMap<Integer, Integer> hashMap = new HashMap<>();
        hashMap.put(0, -1);
        for (int num : list) {
            HashMap<Integer, Integer> tempMap = new HashMap<>(hashMap);
            for (Map.Entry<Integer, Integer> key : hashMap.entrySet()) {
                int tempAns = key.getKey() + queries[num][2];
                if (tempAns > nums[k]) {
                    continue;
                }
                int max = Math.max(key.getValue(), num);
                boolean containsKey = tempMap.containsKey(tempAns);
                if (!containsKey || max < tempMap.get(tempAns)) {
                    tempMap.put(tempAns, max);
                }
            }
            hashMap = tempMap;
        }
        return hashMap;
    }
}

