class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        int len = 0;
        int vit[10]{};
        for (int i = 0; i < n; ++i) if (nums[i] == 0) len++, vit[i] = 1;
        if (len == n) return 0;
        bitset<2000> a[10];
        for (int i = 0; i < n; ++i) a[i][0] = 1;
        for (int i = 0; i < m; ++i) {
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            for (int j = l; j <= r; ++j) {
                if (!vit[j]) {
                    auto t = a[j];
                    a[j] <<= val;
                    a[j] |= t;
                    a[j][val] = 1;
                    if (a[j][nums[j]] == 1) {
                        vit[j] = 1;
                        len++;
                    }   
                }
            }
            if (len == n) return i + 1;
        }
        return -1;
    }
};

