class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n, m = len(nums), len(queries)
        idx = defaultdict(list)
        for j, [x, y, v] in enumerate(queries):
            for i in range(x, y + 1):
                idx[i].append([v, j])

        def calc(arr, target):
            if target == 0: return 0
            if len(arr) == 0: return -1
            na = len(arr)
            dp = [[0] * (target + 1) for _ in range(na)]  # ij
            dp[0][0] = 1
            if arr[0][0] <= target:
                dp[0][arr[0][0]] = 1
                if arr[0][0] == target:
                    return arr[0][1] + 1
            for i in range(1, na):
                dp[i] = dp[i - 1][:]
                x = arr[i][0]
                for j in range(target + 1):
                    dp[i][j] = dp[i - 1][j]
                    if dp[i][j]: continue
                    if j >= x and dp[i - 1][j - x]:
                        dp[i][j] = 1
                        if j == target:
                            return arr[i][1] + 1
            return -1

        ans = 0
        for i, x in enumerate(nums):
            v = calc(idx[i], x)
            if v == -1: return -1
            ans = max(ans, v)
        return ans

