class Solution {
public:
    bool subsetSum(const vector<int>& nums, int target) {
    // dp[i]  i
    std::vector<bool> dp(target + 1, false);
    dp[0] = true; //  0

    for (int num : nums) {
        for (int i = target; i >= num; --i) {
            if (dp[i - num]) {
                dp[i] = true;
            }
        }
    }

    return dp[target];
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        //  k
        int n = nums.size();
        auto check = [&](int k) {
            vector<vector<int>> storeVals(n, vector<int>());
            for (int i = 0; i < k; i++) {
                for (int j = queries[i][0]; j <= queries[i][1]; j++) {
                    storeVals[j].push_back(queries[i][2]);
                }
            }

            for (int i = 0; i < n; i++) {
                if (!subsetSum(storeVals[i], nums[i])) {
                    return false;
                }
            }

            return true;
        };

        int left = 0, right = queries.size();
        while(left <= right){
            int mid = left + (right - left) / 2;
            if(check(mid)){
                right = mid - 1;
            }
            else{
                left = mid + 1;
            }
        }

        return right + 1 == queries.size() + 1? -1 : right + 1;
    }
};

