func minZeroArray(nums []int, queries [][]int) int {
    n:=len(nums)
    arr:=make([][]int,n)
    sum:=make([]int,n)
    total:=0
    for _,v:=range nums{
        total+=v
    }
    if total==0{
        return 0
    }
    for i,v:=range queries{
        for j:=v[0];j<=v[1];j++{
            arr[j]=append(arr[j],v[2])
            sum[j]+=v[2]
        }
        success:=true
        for h:=0;h<n;h++{
            if sum[h]<nums[h]{
                success=false
                break
            }else if sum[h]==nums[h]{
                continue
            }
            if !can(arr[h],nums[h]){
                success=false
                break
            }
        }
        if success{
            return i+1
        }
    }
    return -1
}

func can(arr []int,target int)bool{
    n:=len(arr)
    dp:=make([][]bool,n+1)
    for i:=0;i<=n;i++{
        dp[i]=make([]bool,target+1)
        dp[i][0]=true
    }
    for i:=1;i<=n;i++{
        for j:=1;j<=target;j++{
            if j>=arr[i-1]{
                dp[i][j]=dp[i-1][j] || dp[i-1][j-arr[i-1]]
            }else{
                dp[i][j]=dp[i-1][j]
            }
        }
    }
    return dp[n][target]
}

