            }
        }
        int ans=0;
        for(int i=0;i<n;i++){
            vector<vector<int>> dp(2,vector<int>(1e4+100));
            dp[0][0]=1;
            int res=1100;
            if(ns[i]==0){
                continue;
            }
            int tn=tt[i].size();
            for(int j=0;j<tn;j++){
                int v=tt[i][j][0];
                for(int k=0;k<=ns[i];k++){
                    if(dp[0][k]){
                        dp[1][k+v]=1;
                    }
                }
                for(int k=0;k<=ns[i];k++){
                    dp[0][k]=max(dp[1][k],dp[0][k]);
                    dp[1][k]=0;
                }
                if(dp[0][ns[i]]){
                    res=tt[i][j][1]+1;
                    break;
                }
            }
            ans=max(ans,res);

        }
        if(ans>1000)ans=-1;
        return ans;
    }
};

