        for(c=0;c<a;c++)
            {
                if(nums[c]==0)
                    can[c]=true;
                else
                    fl=false;
            }
        if(fl)
            return 0;
        vector<vector<bool>> dp(a);
        for(c=0;c<a;c++)
            {
                if(!can[c])
                {
                    dp[c].resize(nums[c]+1);
                    dp[c][0]=true;
                }
            }
        for(c=0;c<b;c++)
            {
                for(d=queries[c][0];d<=queries[c][1];d++)
                    {
                        if(!can[d])
                        {
                            for(e=nums[d];e>=1;e--)
                                {
