class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n=len(nums)
        count=sum(int(i==0) for i in nums)
        zeros=[i==0 for i in nums ]
        dp=[[nums[i]] for i in range(n)]
        if count==n:
            return 0
        for i in range(len(queries)):
            l,r,v=queries[i]
            for j in range(l,r+1):
                if zeros[j]:
                    continue
                nx=[]
                for k in dp[j]:
                    if k-v==0:
                        count+=1
                        zeros[j]=True
                        break
                    if k not in nx:
                        nx.append(k)
                    if k-v>0 and k-v not in nx:
                        nx.append(k-v)
                dp[j]=nx
            # print(count,dp)
            if count==n:
                return i+1
        return -1

