func minZeroArray(nums []int, queries [][]int) int {
	theRes := 0
	for index, v := range nums {
		t := f(v, index, queries)
		if t == -1 {
			return -1
		} else {
			theRes = max(theRes, t)
		}
	}
	return theRes
}

func f(x, index int, queries [][]int) int {
	if x == 0 {
		return 0
	}

	theMap := map[int]bool{}
	theMap[x] = true
	for i, q := range queries {
		if q[0] <= index && index <= q[1] {
			keys := make([]int, 0, len(theMap))
			for k := range theMap {
				keys = append(keys, k)
			}
			for _, v := range keys {
				if v-q[2] >= 0 {
					theMap[v-q[2]] = true
				}
			}
		}
		if theMap[0] {
			return i + 1
		}
	}
	return -1
}
func max(a, b int) int {
	if a > b {
		return a
	}
	return b
}
func abs(a int) int {
	if a > 0 {
		return a
	}
	return -a
}

func min(a, b int) int {
	if a < b {
		return a
	}
	return b
}

