class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        m, n = max(nums), len(nums)
        if all(nums[i] == 0 for i in range(n)):
            return 0
        f = [[True] + [False]*(m) for _ in range(n)]

        for idx, (l,r,t) in enumerate(queries):
            for j in range(l, r+1):
                for k in range(m, 0, -1):
                    if k - t >= 0:
                        f[j][k] |= f[j][k-t]

            if all(f[i][x] for i,x in enumerate(nums)):
                return idx+1
        return -1

