            dp = 1
            ok = False
            if nums[i] == 0:
                ans = max(ans, 0)
                continue
            for j in range(len(queries)):
                l, r, val = queries[j]
                if l <= i <= r:
                    dp = dp | (dp << val)
                    dp &= ((1 << (nums[i] + 1)) - 1)
