func minZeroArray(nums []int, queries [][]int) int {
    ans := 0
    for x, num := range nums {
        if num == 0 {
            continue
        }
        vals := make([][2]int, 0)
        for i, query := range queries {
            l, r, val := query[0], query[1], query[2]
            if l <= x && r >= x {
                vals = append(vals, [2]int{val, i})
            }
        }
        m := len(vals)
        // f[i][j] ivalsnumj
        f := make([][]bool, m+1)
        for i := 0; i <= m; i++ {
            f[i] = make([]bool, num+1)
        }
        f[0][num] = true
        res := math.MaxInt
        for i := 1; i <= m; i++ {
            for j := 0; j <= num; j++ {
                f[i][j] = f[i-1][j]
                if j + vals[i-1][0] <= num {
                    f[i][j] = f[i][j] || f[i-1][j+vals[i-1][0]]
                }
            }
            if f[i][0] {
                res = vals[i-1][1]
                break
            }
        }
        if res == math.MaxInt {
            return -1
        }
        ans = max(ans, res+1)
    }
    return ans
}

