class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size(), mx = nums[0];
        for(int v: nums) mx = max(mx, v);


        vector<bool> A(mx + 1, 0);
        int k = 0;
        for(int i = 0; i < n; ++i){
            if(nums[i] == 0) continue;
            fill(A.begin(), A.begin() + nums[i] + 1, 0);
            for(int j = 0; j < m; ++j){
                int v = 0;
                if(queries[j][0] <= i && queries[j][1] >= i) v = queries[j][2];
                for(int k = nums[i]; k - v >= 0; --k) A[k] = A[k] | A[k - v];
                A[v] = 1;
                if(A[nums[i]] == 1) {
                    k = max(k, j + 1);
                    break;
                }
                else if(j + 1 == m) return -1;
            }
        }
        return k;
    }
};

