class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        boolean[] bb=new boolean[nums.length];
        int res=0;
        int a=0;
        for (int i = 0; i < nums.length; i++) {
            if(nums[i]!=0) {
                a++;
                continue;
            }
            bb[i]=true;
            res++;
        }
        if(a==0) return 0;

        Set<Integer>[] sets=new Set[nums.length];
        for (int i = 0; i < sets.length; i++) {
            sets[i]=new HashSet<>();
            sets[i].add(0);
        }
        for(int i=0;i<queries.length;i++){
            int t=queries[i][2];
            for(int l=queries[i][0];l<=queries[i][1];l++){
                if (bb[l]) continue;
                Set<Integer> set=sets[l];
                List<Integer> list=new ArrayList<>();
                list.add(t);
                for (Integer integer : set) {
                    list.add(integer+t);
                }
                set.addAll(list);
                if(set.contains(nums[l])){
                    bb[l]=true;
                    res++;
                }
            }
            if(res==nums.length) return i+1;
        }
        return -1;
    }
}

