class Solution {
    public int minZeroArray(int[] a, int[][] queries) {
        int n = a.length; //10
        int m = queries.length; // 1000
        int s = 0;
        for(int x : a) {
            s += x;
        }
        if(s == 0) {
            return 0;
        }

        boolean dp[][][] = new boolean[n][m][1001];
        for(int i=0; i<n; i++) {
            for(int j=0; j<m; j++) {
                dp[i][j][0] = true;
            }
        }
        for(int i=0; i<n; i++) {
            int l = queries[0][0];
            int r = queries[0][1];
            int val = queries[0][2];
            if(i >= l && i <= r && a[i]-val >= 0) {
                dp[i][0][val] = true;
            }
        }

        for(int i=0; i<n; i++) {
            for(int j=1; j<m; j++) {
                for(int v=1; v<=a[i]; v++) {
                    if(dp[i][j-1][v]) {
                        dp[i][j][v] = true;
                        continue;
                    }
                    int l = queries[j][0];
                    int r = queries[j][1];
                    int val = queries[j][2];
                    if(i >= l && i <= r && v-val >= 0) {
                        if(dp[i][j-1][v-val]) {
                            dp[i][j][v] = true;
                        }
                    } else {
                        dp[i][j][v] = dp[i][j-1][v];
                    }
                }
            }
        } 

        int res = -1;
        for(int i=0; i<n; i++) {
            boolean ok = false;
            for(int j=0; j<m; j++) {
                if(dp[i][j][a[i]]) {
                    res = Math.max(res, j);
                    ok = true;
                    break;
                }
            }
            if(!ok) {
                return -1;
            }
        }
        return res == -1 ? -1 : res+1;
    }
}

