class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();
        vector<vector<int>> cover(n);
        for (int i = 0; i < q; ++i) {
            int l = queries[i][0];
            int r = queries[i][1];
            for (int j = l; j <= r; ++j) {
                if (j >= 0 && j < n) {
                    cover[j].push_back(i);
                }
            }
        }

        vector<int> max_ijs(n, -1);
        for (int j = 0; j < n; ++j) {
            int target = nums[j];
            if (target == 0) {
                max_ijs[j] = -1;
                continue;
            }
            unordered_map<int, int> dp;
            dp[0] = -1;

            for (int i : cover[j]) {
                int val = queries[i][2];
                unordered_map<int, int> new_entries;

                for (auto& [s, i_prev] : dp) {
                    int s_new = s + val;
                    if (dp.find(s_new) == dp.end()) {
                        new_entries[s_new] = i;
                    }
                }

                for (auto& [s_new, i_new] : new_entries) {
                    dp[s_new] = i_new;
                }
            }

            if (dp.find(target) != dp.end()) {
                max_ijs[j] = dp[target];
            } else {
                return -1;
            }
        }

        int k = 0;
        for (int j = 0; j < n; ++j) {
            if (nums[j] != 0) {
                k = max(k, max_ijs[j] + 1);
            }
        }
        return k;
    }
};

