class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();
        vector<vector<pair<int, int>>> cover(n);

        // 
        for (int j = 0; j < q; ++j) {
            int l = queries[j][0], r = queries[j][1], val = queries[j][2];
            if (l > r) continue; // 
            l = max(l, 0);
            r = min(r, n - 1);
            for (int i = l; i <= r; ++i) {
                cover[i].emplace_back(val, j);
            }
        }

        int max_k = -1;
        for (int i = 0; i < n; ++i) {
            if (nums[i] == 0) continue;

            auto& queries_i = cover[i];
            if (queries_i.empty()) return -1;

            // 
            sort(queries_i.begin(), queries_i.end(), [](const pair<int, int>& a, const pair<int, int>& b) {
                return a.second < b.second;
            });

            // valGCD
            int g = 0;
            for (const auto& p : queries_i) {
                g = gcd(g, p.first);
            }
            if (nums[i] % g != 0) return -1;

            // 
            unordered_map<int, int> dp;
            dp[0] = -1; // 0
            for (const auto& [valj, j] : queries_i) {
                unordered_map<int, int> tmp;
                for (const auto& [s, current_max] : dp) {
                    int new_s = s + valj;
                    if (new_s > nums[i]) continue;
                    int new_max = max(current_max, j);
                    if (tmp.find(new_s) == tmp.end() || new_max < tmp[new_s]) {
                        tmp[new_s] = new_max;
                    }
                }
                // tmpdp
                for (const auto& [s, m] : tmp) {
                    if (dp.find(s) == dp.end() || m < dp[s]) {
                        dp[s] = m;
                    }
                }
            }

            if (dp.find(nums[i]) == dp.end()) return -1;
            int current_k = dp[nums[i]] + 1; // k+1
            max_k = max(max_k, current_k);
        }

        // 0
        return max_k == -1 ? 0 : max_k;
    }
};

