class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        vector<int> ans(n);
        for(int i=0;i<n;++i){
            int target = nums[i];
            vector<bool> dp(target+1);
            dp[0] = true;
            int k = 0;
            for(;k<m;++k){
                if(dp[target]) break;
                int x = (i>=queries[k][0] && i<=queries[k][1])?queries[k][2]:0;
                if(x==0) continue;
                for(int j=target;j>=x;--j){
                    dp[j] = dp[j] || dp[j-x];
                }
            }
            ans[i] = dp[target]?k:INT_MAX;
        }
        int k = *max_element(ans.begin(), ans.end());
        return k==INT_MAX?-1:k;
    }
};

