class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        vector<vector<int>> cov(n);
        for (int i = 0; i < m; i++) {
            int l = queries[i][0];
            int r = queries[i][1];
            if (l < 0) l = 0;
            if (r >= n) r = n - 1;
            if (l > r) continue;
            for (int j = l; j <= r; j++) {
                cov[j].push_back(i);
            }
        }
        for (int j = 0; j < n; j++) {
            sort(cov[j].begin(), cov[j].end());
        }
        int mx = -1;
        for (int j = 0; j < n; j++) {
            int t = nums[j];
            if (t == 0) {
                continue;
            }
            vector<int> ct = cov[j];
            unordered_map<int, int> dp;
            dp[0] = -1; 
            for (int idx : ct) {
                int val = queries[idx][2];
                unordered_map<int, int> dp2;
                dp2 = dp;
                for (auto v : dp) {
                    int s = v.first;
                    int cur = v.second;
                    int s2 = s + val;
                    int mx2 = max(cur, idx);
                    if (s2 > t) continue;
                    if (dp2.find(s2) == dp2.end() || mx2 < dp2[s2]) {
                        dp2[s2] = mx2;
                    }
                }
                dp.swap(dp2);
            }
            if (dp.find(t) == dp.end()) {
                return -1;
            } else {
                if (dp[t] > mx) {
                    mx = dp[t];
                }
            }
        }
        return mx == -1 ? 0 : mx + 1;
    }
};

