class Solution {
    int n;
    int nums;
    int[][]qs;
    int m;
    int[][] dp;
    public int dfs(int idx,List<int[]> list,int num){
        if(idx==list.size()) return m+10;
        if(dp[idx][num]>0) return dp[idx][num]; 
        int res=dfs(idx+1,list,num);
        if(num>=list.get(idx)[1]){
            int next=num-list.get(idx)[1];
            if(next==0){
                return list.get(idx)[0];
            }else{
                res=Math.min(res,dfs(idx+1,list,next));
            }
        }
        dp[idx][num]=res;
        return res;
    }
    public int minZeroArray(int[] nums, int[][] qs) {
        this.n=nums.length;
        this.m=qs.length;
        List<int[]>[] list=new ArrayList[n];
        for(int i=0;i<n;i++){
            list[i]=new ArrayList();
        }
        int idx=1;
        for(int[] q:qs){
            for(int i=q[0];i<=q[1];i++){
                list[i].add(new int[]{idx,q[2]});
            }
            idx++;
        }

        int res=0;
        for(int i=0;i<n;i++){
            if(nums[i]==0) continue;
            this.dp=new int[m+5][nums[i]+1];
            res=Math.max(res,dfs(0,list[i],nums[i]));
        }

        return res==m+10?-1:res;    
    }
}

