class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size();
        int m = q.size();
        int l = 0, r = m;
        auto check = [&](int k)->bool{
            vector<vector<int>> left(n), right(n);
            for(int i = 0; i < k; i++){
                auto& qq = q[i];
                left[qq[0]].push_back(qq[2]);
                right[qq[1]].push_back(qq[2]);
            }
            multiset<int> st;
            for(int i = 0; i < n; i++){
                for(auto x : left[i]){
                    st.insert(x);
                }
                vector<bool> f(nums[i] + 1);
                f[0] = true;
                for(auto x : st){
                    for(int j = nums[i]; j >= x; j--){
                        f[j] = f[j] || f[j - x];
                    }
                }
                if(!f[nums[i]]) return false;
                for(auto x : right[i]){
                    st.erase(st.find(x));
                }
            }
            return true;
        };
        while(l <= r){
            int m = l + (r - l)/2;
            // cout << m << " " << check(m) << endl;
            if(check(m)) r = m - 1;
            else l = m + 1;
        }
        return l > m ? -1 : l;
    }
};

