class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        std::vector<std::vector<bool>> record(n);
        std::vector<int> maxIndex(n, 0);
        std::vector<bool> couldZero(n, false);
        int zeroCount = 0;
        for (int index = 0; index < n; ++index) {
            if (nums[index] == 0) {
                couldZero[index] =  true;
                zeroCount++;
                continue;
            }
            record[index] = std::vector<bool>(nums[index] + 1, false);
            record[index][0] = true;
        }
        if (zeroCount == n) {
            return 0;
        }
        for (int i = 0; i < queries.size(); ++i) { // 1000
            // printf("i %drn", i);
            std::vector<int>& quer = queries[i];
            for (int j = quer[0]; j <= quer[1]; ++j) { // 10
                // printf("j %drn", j);
                int val = quer[2];
                if (couldZero[j]) {
                    continue;
                }
                std::vector<bool> temp = record[j];
                int tempMax = maxIndex[j];
                for (int k = 0; k <= tempMax; ++k) { // 1000
                    // printf("k %d m %d k + val %d nums[j] %d %drn", k, maxIndex[j], k + val, nums[j], record[j][k]);
                    if (record[j][k] && ((k + val) <= nums[j])) {
                        temp[k + val] = true;
                        maxIndex[j] = std::max(maxIndex[j], k + val);
                        if ((k + val) == nums[j]) {
                            couldZero[j] = true;
                            break;
                        }
                    }
                }
                record[j] = temp;
                // printf("rn");
            }
            int count = 0;
            for (int j = 0; j < n; ++j) {
                count += couldZero[j] ? 1 : 0;
            }
            if (count == n) {
                return i + 1;
            }
        }
        return -1;
    }
};

