class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)
        def check(k: int) -> bool:
            for i in range(n):
                dp = 1
                for j in range(k):
                    l, r, v = queries[j]
                    if l <= i <= r:
                        dp |= dp << v
                if ((dp >> nums[i]) & 1) == 0:
                    return False
            return True

        low, high = 0, m + 1
        res = -1
        while low < high:
            mid = (low + high) // 2
            if check(mid):
                res = mid
                high = mid
            else:
                low = mid + 1
        return res if res != -1 and res <= m else -1

