class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)

        ans = [inf] * n

        for i in range(n):
            if nums[i] == 0:
                ans[i] = 0
                continue
            s = set()
            s.add(nums[i])
            for j in range(m):
                l, r, v = queries[j]
                if l <= i <= r:
                    t = set()
                    for x in s:
                        t.add(x)
                        t.add(x - v)
                    if 0 in t:
                        ans[i] = j + 1
                        break
                    s = t
        mn = max(ans)
        return mn if mn < inf else -1

