class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int ans = -1;

        for (int i = 0; i < nums.length; i++) {
            int minK = this.getMinK(nums[i], i, queries);
            if (minK == -1) {
                return -1;
            } else if (ans == -1) {
                ans = minK;
            } else {
                ans = Math.max(ans, minK);
            }
        }

        return ans;
    }

    private int getMinK(int num, int target, int[][] queries) {
        if (num == 0) {
            return 0;
        }

        Set<Integer> availableValue = new HashSet<>();
        availableValue.add(num);

        for (int ptr = 0; ptr < queries.length; ptr++) {
            int[] query = queries[ptr];
            if (target < query[0] || target > query[1]) {
                continue;
            }

            Set<Integer> nextDp = new HashSet<>();
            for (Integer value : availableValue) {
                if (value == query[2]) {
                    return ptr + 1;
                }
                if (value > query[2]) {
                    nextDp.add(value - query[2]);
                }
            }
            nextDp.addAll(availableValue);
            availableValue = nextDp;
        }

        return -1;
    }
}

