            if nums[i]==0:
                dp[i]=0
        qs=len(queries)
        for q in range(qs):
            for i in range(queries[q][0],queries[q][1]+1):
                if dp[i]>=0:
                    continue
                val=queries[q][2]
                ds_update=[]
                for k in ds[i].keys():
                    tmp=k-val
                    if tmp==0:
                        dp[i]=q+1
                        break
                    elif tmp>0 and tmp not in ds[i]:
                        ds_update.append(tmp)
                for k in ds_update:
                    ds[i][k]=1
        if min(dp)==-1:
            return -1
        else:
            return max(dp)

