                    if tt == 0:
                        return True

                    if arr[ii] > tt:
                        return False

                    if dp(ii+1, tt):
                        return True

                    if tt >= arr[ii] and dp(ii+1, tt-arr[ii]):
                        return True

                    return False


                if not dp(0, tar):
                    return False
                else:
                    ok[x] = 1

            return True





        cnt = 0
        for a, b, v in queries:
            cnt += 1

            for i in range(a, b+1):
                vals[i].append(v)

            if check():
                return cnt

        return -1

