class Solution {
public:
    int minZeroArray(vector<int> nums, vector<vector<int>> queries) {
        int n = nums.size(), m = queries.size();
        int l = 0, r = m, up = 1000;
        vector dp(m+1,vector(n+1,vector<int>(up+1,0)));
        for(int i=0;i<n;i++) dp[0][i][0] = 1;
        for(int i=1;i<=m;i++){
            int l = queries[i-1][0], r = queries[i-1][1], val = queries[i-1][2];
            dp[i] = dp[i-1];
            for(int j=l;j<=r;j++){
                for(int k=0;k<=up;k++){
                    if(k >= val) dp[i][j][k] |= dp[i-1][j][k-val];
                }
            }
        }
        int maxn = 0;
        for(int i=0;i<n;i++){
            int x = m + 1;
            for(int j=0;j<=m;j++){
                if(dp[j][i][nums[i]])  {
                    x = j;
                    break;
                }
            }
            maxn = max(maxn,x);
        }
        int ans = -1;
        if(maxn <= m) ans = maxn;
        return ans;
    }
};

