class Solution {
public:
    bool dfs(vector<int>& f, int tar) {
        int n = f.size();
        vector<bool> dp(tar + 1, false);
        dp[0] = true;
        for (int i = 0; i < n; i++) {
            for (int j = tar; j >= f[i]; j--) {
                dp[j] = dp[j] || dp[j - f[i]];
            }
        }
        return dp[tar];
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        function<bool(int)> check = [&](int tar) -> bool {
            vector<vector<int>> f(n);
            for (int i = 0; i <= tar; i++) {
                for (int j = queries[i][0]; j <= queries[i][1]; j++) {
                    f[j].push_back(queries[i][2]);
                }
            }
            bool a = true;
            for (int i = 0; i < n; i++) {
                if (dfs(f[i], nums[i]) == false) {
                    a = false;
                }
            }
            return a;
        };
        int l = -1, r = queries.size();
        int ans = 0;
        while (l < r) {
            int mid = l + (r - l) / 2;
            if (check(mid)) {
                r = mid;
            } else {
                l = mid + 1;
            }
        }
        if (l == queries.size()) {
            return -1;
        }
        return l + 1;
    }
};

