                        // cout << k << ' ';
                        break;
                    }
                }
                while(q.size()) {
                    s.insert(q.front());
                    q.pop();
                }
                // cout << 'n';

                if(!s.count(vec[2])) s.insert(vec[2]);
                if(s.count(nums[i])) break;
            }

            // cout << 'n';
            if(s.count(nums[i]) == 0) {
                // cout << nums[i] << 'n';
                ans = -1;
                break;
            }
            // cout << k << ' ';
            ans = max(ans, k + 1);
        }

        return ans;
    }
