class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        boolean[] isZero = new boolean[n];
        boolean isAllZero = true;
        for(int i = 0;i < n;i++){
            if(nums[i] != 0){
                isAllZero = false;
            }else {
                isZero[i] = true;
            }
        }
        if(isAllZero){
            return 0;
        }
        List<Set<Integer>> map = new ArrayList<>(n);
        for(int i = 0;i < n;i++){
            HashSet<Integer> set = new HashSet<>();
            set.add(0);
            map.add(set);
        }
        int ans = -1;
        for(int i = 0;i < queries.length;i++){
            for(int j = queries[i][0];j <= queries[i][1];j++){
                if(isZero[j]){
                   continue;
                }
                if(map.get(j).contains(nums[j] - queries[i][2])){
                    isZero[j] = true;
                    continue;
                }
                Set<Integer> set = new HashSet<>();
                for(int key:map.get(j)){
                    set.add(key+queries[i][2]);
                }
                map.get(j).addAll(set);

            }
            boolean isOver = true;
            for (int j = 0; j < n; j++) {
                if(!isZero[j]){
                    isOver = false;
                    break;
                }
            }
            if(isOver){
                ans = i+1;
                break;
            }
        }
        return ans;
    }
}

