class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        @cache
        def dfs(index,num, k):
            if num == 0:
                return True
            if k < 0:
                return False

            ans = False
            left, right ,val = queries[k][0],queries[k][1],queries[k][2]
            #
            ans = ans or dfs(index,num, k - 1)
            #
            if index >= left and right >= index:
                if num >= val:
                    ans = ans or dfs(index, num - val, k - 1)
            return ans

        if len(set([each for each in nums]) ) == 1 and nums[0] == 0:
            return 0


        left,right = 0, len(queries) - 1
        while left <= right:
            mid = left + (right - left)//2
            curr = True
            for i,num in enumerate(nums):
                curr_ans = dfs(i,num, mid)
                curr = curr and curr_ans

            if curr:
                right = mid - 1
            else:
                left = mid + 1
        if left == len(queries):
            return -1
        return left + 1

