import "slices"

func minZeroArray(nums []int, queries [][]int) int {
	if slices.Max(nums) == 0 {
		return 0
	}
	n := len(nums)
	cnt := make([]map[int]bool, n)
	for i := range cnt {
		cnt[i] = map[int]bool{nums[i]: true}
	}
	for i, v := range queries {
		for j := v[0]; j <= v[1]; j++ {
			if cnt[j][0] {
				continue
			}
			keys := make([]int, len(cnt[j]))
			c := 0
			for z := range cnt[j] {
				keys[c] = z
				c++
			}
			for _, z := range keys {
				if z-v[2] < 0 {
					continue
				}
				cnt[j][z-v[2]] = true
				//fmt.Printf("i:%v j:%v z:%v set true:%vn", i, j, z, z-v[2])

			}
		}
		if isValid(cnt) {
			return i + 1
		}
	}
	return -1
}

func isValid(cnt []map[int]bool) bool {
	for _, v := range cnt {
		if !v[0] {
			return false
		}
	}
	return true
